    # BB-IBE

    alpha, b, bp, b0, b1, r, x, y, s, sp = symbols('alpha, b, bp, b0, b1, r, x, y, s, sp')

    k1 = alpha + r*(b0 + y*b1)
    k2 = r
    c1 = s*(b0 + x*b1)
    c2 = s
    mpk1 = b0
    mpk2 = b1
    
    # known values: x, y

    unknown = [alpha, b0, b1, r, s]

    k = [k1, k2]
    c = [c1, c2]
    mpk = [mpk1, mpk2]

    #security_analysis(alpha, s, k, c, mpk, unknown, [], [])
    
    security_attack = SecurityAttack()
    security_attack.init(alpha*s, k, c, mpk, unknown)
    security_attack.run()
    print("\n[*] Security analysis results:\n")
    print("\n" + security_attack.show_solution())
    security_attack.show_proof()

    # BB-IBE alt

    alpha, b, bp, b0, b1, r, x, y, s, sp = symbols('alpha, b, bp, b0, b1, r, x, y, s, sp')

    k1 = alpha + r*b
