    k3 = r
    c1 = sp*(b0 + x*b1) + s*b
    c2 = s
    c3 = sp
    mpk1 = b0
    mpk2 = b1
    mpk3 = b
    
    # known values: x, y

    unknown = [alpha, b, b0, b1, r, s, sp]

    k = [k1, k2, k3]
    c = [c1, c2, c3]
    mpk = [mpk1, mpk2, mpk3]

    #security_analysis(alpha, s, k, c, mpk, unknown, [], [])

